import React, { useState, useEffect } from 'react';
import { Container, Row, Col, Card, Button, Form, Alert } from 'react-bootstrap';
import { useNavigate } from 'react-router-dom';

const Plans = () => {
  const [plans, setPlans] = useState([]);
  const [filteredPlans, setFilteredPlans] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [priceRange, setPriceRange] = useState({ min: 0, max: 1000 });

  const navigate = useNavigate();

  useEffect(() => {
    // Получение всех доступных планов
    fetchPlans();
  }, []);

  // Применяем фильтр цены при изменении priceRange
  useEffect(() => {
    if (plans.length > 0) {
      setFilteredPlans(
        plans.filter(plan => 
          plan.price >= priceRange.min && 
          plan.price <= priceRange.max
        )
      );
    }
  }, [priceRange, plans]);

  const fetchPlans = async () => {
    setLoading(true);
    setError('');

    try {
      const response = await fetch('/api/plans');
      const data = await response.json();

      if (!response.ok) {
        throw new Error(data.error || 'Не удалось загрузить тарифы');
      }

      setPlans(data.plans || []);
      setFilteredPlans(data.plans || []);
    } catch (err) {
      setError(err.message);
    } finally {
      setLoading(false);
    }
  };

  const handlePriceRangeChange = (e) => {
    const { name, value } = e.target;
    setPriceRange(prev => ({
      ...prev,
      [name]: parseFloat(value)
    }));
  };

  const handleSubscribe = (planId) => {
    // Проверяем авторизацию пользователя
    const token = localStorage.getItem('token');

    if (!token) {
      navigate('/login', { state: { redirectTo: `/plans/${planId}` } });
      return;
    }

    // Перенаправляем на страницу оформления подписки
    navigate(`/subscribe/${planId}`);
  };

  const formatFeatures = (featuresString) => {
    if (!featuresString) return [];

    try {
      return JSON.parse(featuresString);
    } catch {
      // Если не валидный JSON, разделяем по запятым или возвращаем как одну функцию
      return featuresString.includes(',') 
        ? featuresString.split(',').map(f => f.trim())
        : [featuresString];
    }
  };

  // Форматирование периода подписки
  const formatPeriod = (days) => {
    if (days === 30) return 'месяц';
    if (days === 90) return '3 месяца';
    if (days === 180) return '6 месяцев';
    if (days === 365) return 'год';
    return `${days} дней`;
  };

  return (
    <Container className="py-5">
      <h1 className="text-center mb-5">Тарифы подписок</h1>

      {error && <Alert variant="danger">{error}</Alert>}

      <Row className="mb-4">
        <Col md={6} lg={4}>
          <Card>
            <Card.Body>
              <h5>Фильтр тарифов</h5>
              <Form>
                <Form.Group className="mb-3">
                  <Form.Label>Диапазон цен (₽)</Form.Label>
                  <div className="d-flex align-items-center">
                    <Form.Control 
                      type="number" 
                      placeholder="От" 
                      name="min"
                      value={priceRange.min}
                      onChange={handlePriceRangeChange}
                      min="0"
                    />
                    <span className="mx-2">до</span>
                    <Form.Control 
                      type="number" 
                      placeholder="До" 
                      name="max"
                      value={priceRange.max}
                      onChange={handlePriceRangeChange}
                      min="0"
                    />
                  </div>
                </Form.Group>
              </Form>
            </Card.Body>
          </Card>
        </Col>
      </Row>

      {loading ? (
        <div className="text-center my-5">
          <div className="spinner-border" role="status">
            <span className="visually-hidden">Загрузка...</span>
          </div>
        </div>
      ) : filteredPlans.length > 0 ? (
        <Row>
          {filteredPlans.map(plan => (
            <Col md={4} key={plan.id} className="mb-4">
              <Card className="plan-card h-100">
                <Card.Header as="h5" className="bg-primary text-white">{plan.name}</Card.Header>
                <Card.Body className="d-flex flex-column">
                  <Card.Title className="mb-3">
                    <span className="display-6">{plan.price.toFixed(2)} ₽</span>
                    <small className="text-muted"> / {formatPeriod(plan.duration)}</small>
                  </Card.Title>
                  <Card.Text>{plan.description}</Card.Text>

                  <div className="mt-3 mb-4">
                    <h6>В подписку входит:</h6>
                    <ul>
                      {formatFeatures(plan.features).map((feature, index) => (
                        <li key={index}>{feature}</li>
                      ))}
                    </ul>
                  </div>

                  <Button 
                    variant="primary" 
                    onClick={() => handleSubscribe(plan.id)}
                    className="mt-auto"
                  >
                    Оформить подписку
                  </Button>
                </Card.Body>
              </Card>
            </Col>
          ))}
        </Row>
      ) : (
        <div className="text-center my-5">
          <p>Нет тарифов, соответствующих вашим критериям.</p>
        </div>
      )}
    </Container>
  );
};

export default Plans;
