import React, { useState, useEffect } from 'react';
import { Container, Row, Col, Card, Button, Form, Alert, Badge, InputGroup } from 'react-bootstrap';
import { useNavigate } from 'react-router-dom';
import { Search } from 'react-bootstrap-icons';
import { plansAPI, subscriptionsAPI } from '../utils/api';

// Предопределенные популярные сервисы подписок
const POPULAR_SERVICES = [
  { 
    id: 'yandex-plus', 
    name: 'Яндекс Плюс',
    logo: 'https://avatars.mds.yandex.net/get-bunker/60661/4b62ceb27f3631e5ca8d2e35e70dd549e17b9a64/svg',
    color: '#ffcc00' 
  },
  { 
    id: 'google-one', 
    name: 'Google One',
    logo: 'https://lh3.googleusercontent.com/qExG0iBAH9TbIBljQnKJJJ0T4cMPtJsGIK2vgNO2SSgNh2QwrLsAkXUQC8eh_9QXMw',
    color: '#4285F4' 
  },
  { 
    id: 'spotify', 
    name: 'Spotify Premium',
    logo: 'https://storage.googleapis.com/pr-newsroom-wp/1/2018/11/Spotify_Logo_RGB_Green.png',
    color: '#1ED760' 
  },
  { 
    id: 'netflix', 
    name: 'Netflix',
    logo: 'https://assets.nflxext.com/ffe/siteui/common/icons/nficon2016.png',
    color: '#E50914' 
  },
  { 
    id: 'apple-one', 
    name: 'Apple One',
    logo: 'https://www.apple.com/v/apple-one/a/images/meta/apple-one__bctggt8g2b3m_og.png',
    color: '#A2AAAD' 
  },
  {
    id: 'kinopoisk',
    name: 'Кинопоиск HD',
    logo: 'https://avatars.mds.yandex.net/get-bunker/56833/7b83483802851da1a0611d01c3247486e08da9f9/svg',
    color: '#f60'
  },
  {
    id: 'amediateka',
    name: 'Amediateka',
    logo: 'https://www.amediateka.ru/static/images/logo/amediateka-logo.svg',
    color: '#000000'
  },
  {
    id: 'ivi',
    name: 'IVI',
    logo: 'https://upload.wikimedia.org/wikipedia/commons/thumb/a/a9/IVI_2022.svg/1200px-IVI_2022.svg.png',
    color: '#ea003d'
  },
  {
    id: 'premier',
    name: 'Premier',
    logo: 'https://premier.one/img/premier.svg',
    color: '#6236ff'
  }
];

const Plans = () => {
  const [plans, setPlans] = useState([]);
  const [filteredPlans, setFilteredPlans] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [searchQuery, setSearchQuery] = useState('');
  const [selectedCategory, setSelectedCategory] = useState('all');
  const [actionSuccess, setActionSuccess] = useState('');

  const navigate = useNavigate();

  useEffect(() => {
    fetchPlans();
  }, []);

  // Применяем фильтр при изменении запроса или категории
  useEffect(() => {
    if (plans.length > 0) {
      let filtered = [...plans];

      // Применяем поиск по имени
      if (searchQuery) {
        filtered = filtered.filter(plan => 
          plan.name.toLowerCase().includes(searchQuery.toLowerCase())
        );
      }

      // Применяем фильтр по категории
      if (selectedCategory !== 'all') {
        filtered = filtered.filter(plan => {
          // Здесь нужно соотнести план с категорией
          // Это примерная логика, в реальности может потребоваться более сложная проверка
          const serviceName = plan.name.toLowerCase();
          return serviceName.includes(selectedCategory.toLowerCase());
        });
      }

      setFilteredPlans(filtered);
    }
  }, [searchQuery, selectedCategory, plans]);

  const fetchPlans = async () => {
    setLoading(true);
    setError('');

    try {
      const data = await plansAPI.getAll();
      const availablePlans = data.plans || [];

      setPlans(availablePlans);
      setFilteredPlans(availablePlans);
    } catch (err) {
      setError(err.message);
    } finally {
      setLoading(false);
    }
  };

  const handleSubscribe = async (planId) => {
    // Проверяем авторизацию пользователя
    const token = localStorage.getItem('token');

    if (!token) {
      navigate('/login', { state: { redirectTo: '/plans' } });
      return;
    }

    // Сразу оформляем подписку
    try {
      setLoading(true);
      const response = await subscriptionsAPI.subscribe(planId);
      setActionSuccess('Подписка успешно оформлена!');
      // После успешной подписки перенаправляем на дашборд
      setTimeout(() => navigate('/dashboard'), 1500);
    } catch (err) {
      setError(err.message);
    } finally {
      setLoading(false);
    }
  };

  const formatFeatures = (featuresString) => {
    if (!featuresString) return [];

    try {
      return JSON.parse(featuresString);
    } catch {
      // Если не валидный JSON, разделяем по запятым или возвращаем как одну функцию
      return featuresString.includes(',') 
        ? featuresString.split(',').map(f => f.trim())
        : [featuresString];
    }
  };

  // Форматирование периода подписки - фокус только на месяцах
  const formatPeriod = (days) => {
    if (days === 30 || days === 31) return 'месяц';
    return `${days} дней`;
  };

  return (
    <Container className="py-5">
      <h1 className="text-center mb-5">Популярные сервисы подписок</h1>

      {error && <Alert variant="danger" dismissible onClose={() => setError('')}>
        <Alert.Heading>Ошибка!</Alert.Heading>
        <p>{error}</p>
      </Alert>}

      {actionSuccess && <Alert variant="success" dismissible onClose={() => setActionSuccess('')}>
        <Alert.Heading>Успешно!</Alert.Heading>
        <p>{actionSuccess}</p>
      </Alert>}

      <Row className="mb-4">
        <Col md={6}>
          <Form.Group>
            <InputGroup>
              <Form.Control 
                type="text" 
                placeholder="Поиск по названию сервиса..." 
                value={searchQuery}
                onChange={(e) => setSearchQuery(e.target.value)}
              />
              <Button variant="outline-secondary">
                <Search />
              </Button>
            </InputGroup>
          </Form.Group>
        </Col>
        <Col md={6}>
          <Form.Group>
            <Form.Select 
              value={selectedCategory}
              onChange={(e) => setSelectedCategory(e.target.value)}
            >
              <option value="all">Все сервисы</option>
              <option value="video">Видеосервисы</option>
              <option value="music">Музыкальные</option>
              <option value="storage">Облачные хранилища</option>
              <option value="other">Другое</option>
            </Form.Select>
          </Form.Group>
        </Col>
      </Row>

      {/* Секция популярных сервисов */}
      <div className="mb-5">
        <h3 className="mb-3">Популярные сервисы</h3>
        <Row className="g-4">
          {POPULAR_SERVICES.map(service => (
            <Col key={service.id} xs={6} md={3} lg={2}>
              <Card 
                className="service-icon-card text-center h-100"
                onClick={() => setSearchQuery(service.name)}
                style={{ cursor: 'pointer', borderColor: service.color }}
              >
                <Card.Body className="d-flex flex-column justify-content-center align-items-center">
                  <div className="service-logo mb-2" style={{ height: '50px', width: '100%', textAlign: 'center' }}>
                    <img 
                      src={service.logo} 
                      alt={service.name} 
                      style={{ maxHeight: '100%', maxWidth: '100%', objectFit: 'contain' }}
                      onError={(e) => { e.target.src = 'https://via.placeholder.com/100'; }}
                    />
                  </div>
                  <div className="service-name">
                    {service.name}
                  </div>
                </Card.Body>
              </Card>
            </Col>
          ))}
        </Row>
      </div>

      {loading ? (
        <div className="text-center my-5">
          <div className="spinner-border" role="status">
            <span className="visually-hidden">Загрузка...</span>
          </div>
        </div>
      ) : filteredPlans.length > 0 ? (
        <Row className="g-4">
          {filteredPlans.map(plan => (
            <Col md={6} lg={4} key={plan.id} className="mb-4">
              <Card className="plan-card h-100">
                <Card.Header as="h5" className="d-flex justify-content-between align-items-center">
                  {plan.name}
                  {plan.is_popular && (
                    <Badge bg="warning" text="dark">Популярный</Badge>
                  )}
                </Card.Header>
                <Card.Body className="d-flex flex-column">
                  <Card.Title className="mb-3">
                    <span className="display-6">{plan.price.toFixed(2)} ₽</span>
                    <small className="text-muted"> / {formatPeriod(plan.duration)}</small>
                  </Card.Title>
                  <Card.Text>{plan.description}</Card.Text>

                  <div className="mt-3 mb-4">
                    <h6>Что включено:</h6>
                    <ul>
                      {formatFeatures(plan.features).map((feature, index) => (
                        <li key={index}>{feature}</li>
                      ))}
                    </ul>
                  </div>

                  <Button 
                    variant="primary" 
                    onClick={() => handleSubscribe(plan.id)}
                    className="mt-auto"
                    disabled={loading}
                  >
                    {loading ? 'Подождите...' : 'Оформить подписку'}
                  </Button>
                </Card.Body>
              </Card>
            </Col>
          ))}
        </Row>
      ) : (
        <div className="text-center my-5">
          <p>Нет подписок, соответствующих вашим критериям.</p>
          <Button variant="outline-secondary" onClick={() => {
            setSearchQuery('');
            setSelectedCategory('all');
          }}>
            Сбросить фильтры
          </Button>
        </div>
      )}
    </Container>
  );
};

export default Plans;
