import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { Container, Row, Col, Card, Button, Badge } from 'react-bootstrap';
import { plansAPI } from '../utils/api';

// Логотипы сервисов для слайдера на главной странице
const serviceLogos = [
  {
    name: 'Яндекс Плюс',
    logo: 'https://avatars.mds.yandex.net/get-bunker/60661/4b62ceb27f3631e5ca8d2e35e70dd549e17b9a64/svg'
  },
  {
    name: 'Netflix',
    logo: 'https://assets.nflxext.com/ffe/siteui/common/icons/nficon2016.png'
  },
  {
    name: 'Spotify',
    logo: 'https://storage.googleapis.com/pr-newsroom-wp/1/2018/11/Spotify_Logo_RGB_Green.png'
  },
  {
    name: 'Apple One',
    logo: 'https://www.apple.com/v/apple-one/a/images/meta/apple-one__bctggt8g2b3m_og.png'
  },
  {
    name: 'Google One',
    logo: 'https://lh3.googleusercontent.com/qExG0iBAH9TbIBljQnKJJJ0T4cMPtJsGIK2vgNO2SSgNh2QwrLsAkXUQC8eh_9QXMw'
  },
  {
    name: 'Кинопоиск',
    logo: 'https://avatars.mds.yandex.net/get-bunker/56833/7b83483802851da1a0611d01c3247486e08da9f9/svg'
  }
];

const Home = () => {
  const [popularPlans, setPopularPlans] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    // Загружаем популярные планы
    const fetchPopularPlans = async () => {
      try {
        const data = await plansAPI.getAll();
        // Отображаем только первые 3 плана как "популярные"
        const plans = data.plans || [];
        // Сортируем по флагу is_popular если он есть
        const sortedPlans = plans.sort((a, b) => (b.is_popular || 0) - (a.is_popular || 0));
        setPopularPlans(sortedPlans.slice(0, 3));
      } catch (error) {
        console.error('Error fetching plans:', error);
      } finally {
        setLoading(false);
      }
    };

    fetchPopularPlans();
  }, []);

  // Форматирование периода подписки
  const formatPeriod = (days) => {
    if (days === 30 || days === 31) return 'месяц';
    return `${days} дней`;
  };

  return (
    <div>
      <div className="bg-light py-5">
        <Container>
          <Row className="align-items-center">
            <Col lg={6}>
              <h1 className="display-4 fw-bold">Управляйте всеми подписками в одном месте</h1>
              <p className="lead">
                Централизованное управление подписками на популярные сервисы: Яндекс Плюс, Netflix, Spotify, 
                Google One и многие другие. Отслеживайте сроки, экономьте деньги и не пропускайте важные платежи.
              </p>
              <div className="d-grid gap-2 d-md-flex justify-content-md-start mt-4">
                <Button as={Link} to="/plans" variant="primary" size="lg" className="px-4 me-md-2">Выбрать сервис</Button>
                <Button as={Link} to="/register" variant="outline-secondary" size="lg" className="px-4">Начать пользоваться</Button>
              </div>
            </Col>
            <Col lg={6} className="mt-4 mt-lg-0">
              <div className="service-logos-container p-4 bg-white rounded shadow">
                <h4 className="text-center mb-4">Поддерживаемые сервисы</h4>
                <Row className="g-4 justify-content-center">
                  {serviceLogos.map((service, index) => (
                    <Col key={index} xs={4} md={4} className="text-center">
                      <div className="service-logo mb-2" style={{ height: '50px' }}>
                        <img 
                          src={service.logo} 
                          alt={service.name} 
                          style={{ maxHeight: '100%', maxWidth: '100%', objectFit: 'contain' }}
                          onError={(e) => { e.target.src = 'https://via.placeholder.com/100'; }}
                        />
                      </div>
                      <div className="service-name small">{service.name}</div>
                    </Col>
                  ))}
                </Row>
              </div>
            </Col>
          </Row>
        </Container>
      </div>

      <Container className="py-5">
        <Row className="mb-5">
          <h2 className="text-center mb-5">Почему стоит управлять подписками с нами?</h2>

          <Col md={4} className="mb-4">
            <Card className="h-100 text-center">
              <Card.Body>
                <div className="feature-icon mb-3 text-primary">
                  <i className="bi bi-bell fs-1">🔔</i>
                </div>
                <Card.Title>Напоминания о продлении</Card.Title>
                <Card.Text>
                  Больше никаких пропущенных платежей. Получайте уведомления перед автоматическим продлением ваших подписок.
                </Card.Text>
              </Card.Body>
            </Card>
          </Col>

          <Col md={4} className="mb-4">
            <Card className="h-100 text-center">
              <Card.Body>
                <div className="feature-icon mb-3 text-primary">
                  <i className="bi bi-graph-up-arrow fs-1">📊</i>
                </div>
                <Card.Title>Анализ расходов</Card.Title>
                <Card.Text>
                  Понимайте структуру ваших расходов и оптимизируйте затраты на подписки с помощью детальной аналитики.
                </Card.Text>
              </Card.Body>
            </Card>
          </Col>

          <Col md={4} className="mb-4">
            <Card className="h-100 text-center">
              <Card.Body>
                <div className="feature-icon mb-3 text-primary">
                  <i className="bi bi-cloud-check fs-1">☁️</i>
                </div>
                <Card.Title>Централизованное управление</Card.Title>
                <Card.Text>
                  Управляйте всеми подписками в одном месте. Отменяйте, продлевайте и отслеживайте все ежемесячные платежи.
                </Card.Text>
              </Card.Body>
            </Card>
          </Col>
        </Row>

        <Row className="mt-5">
          <h2 className="text-center mb-5">Популярные ежемесячные подписки</h2>

          {loading ? (
            <div className="text-center">
              <div className="spinner-border" role="status">
                <span className="visually-hidden">Загрузка...</span>
              </div>
            </div>
          ) : popularPlans.length > 0 ? (
            popularPlans.map(plan => (
              <Col md={4} key={plan.id} className="mb-4">
                <Card className="plan-card h-100">
                  <Card.Header as="h5" className="d-flex justify-content-between align-items-center">
                    {plan.name}
                    {plan.is_popular && (
                      <Badge bg="warning" text="dark">Популярный</Badge>
                    )}
                  </Card.Header>
                  <Card.Body className="d-flex flex-column">
                    <Card.Title className="mb-3">
                      <span className="display-6">{plan.price.toFixed(2)} ₽</span>
                      <small className="text-muted"> / {formatPeriod(plan.duration)}</small>
                    </Card.Title>
                    <Card.Text>{plan.description}</Card.Text>
                    <div className="mt-auto">
                      <Button as={Link} to="/plans" variant="outline-primary">Подробнее</Button>
                    </div>
                  </Card.Body>
                </Card>
              </Col>
            ))
          ) : (
            <p className="text-center">Сейчас нет доступных планов.</p>
          )}

          <div className="text-center mt-4">
            <Button as={Link} to="/plans" variant="primary">Посмотреть все сервисы</Button>
          </div>
        </Row>

        <Row className="mt-5 py-5 bg-light rounded">
          <Col lg={8} className="mx-auto text-center">
            <h2>Начните управлять своими подписками уже сегодня</h2>
            <p className="lead">
              Регистрация занимает всего пару минут. После этого вы сможете добавлять, отменять и отслеживать 
              подписки на любимые сервисы и сэкономить до 30% ежемесячных расходов.
            </p>
            <Button as={Link} to="/register" variant="primary" size="lg" className="mt-3">
              Зарегистрироваться бесплатно
            </Button>
          </Col>
        </Row>
      </Container>
    </div>
  );
};

export default Home;
