import React, { useState, useEffect } from 'react';
import { Container, Row, Col, Card, Button, Alert, Badge, Tabs, Tab, Form, InputGroup, Dropdown } from 'react-bootstrap';
import { Link } from 'react-router-dom';
import { Search, XCircle, SortDown, ArrowDownUp } from 'react-bootstrap-icons';
import { subscriptionsAPI } from '../utils/api';

// Ð¡Ð»Ð¾Ð²Ð°Ñ€ÑŒ Ð¸ÐºÐ¾Ð½Ð¾Ðº Ð´Ð»Ñ Ñ€Ð°Ð·Ð½Ñ‹Ñ… ÑÐµÑ€Ð²Ð¸ÑÐ¾Ð² Ð¿Ð¾Ð´Ð¿Ð¸ÑÐ¾Ðº
const SERVICE_ICONS = {
  'ÑÐ½Ð´ÐµÐºÑ': 'ðŸ”',
  'netflix': 'ðŸ“º',
  'spotify': 'ðŸŽµ',
  'apple': 'ðŸŽ',
  'google': 'â˜ï¸',
  'ÐºÐ¸Ð½Ð¾Ð¿Ð¾Ð¸ÑÐº': 'ðŸŽ¬',
  'Ð°Ð¼ÐµÐ´Ð¸Ð°Ñ‚ÐµÐºÐ°': 'ðŸ“½ï¸',
  'amediateka': 'ðŸ“½ï¸',
  'ivi': 'ðŸŽ¦',
  'premier': 'ðŸŽžï¸',
  'default': 'ðŸ“±' // Ð¸ÐºÐ¾Ð½ÐºÐ° Ð¿Ð¾ ÑƒÐ¼Ð¾Ð»Ñ‡Ð°Ð½Ð¸ÑŽ
};

const Dashboard = ({ user }) => {
  const [subscriptions, setSubscriptions] = useState([]);
  const [activeSubscriptions, setActiveSubscriptions] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [actionSuccess, setActionSuccess] = useState('');
  const [stats, setStats] = useState({
    active_count: 0,
    total_monthly_spending: 0
  });

  // Ð¡Ð¾ÑÑ‚Ð¾ÑÐ½Ð¸Ñ Ð´Ð»Ñ Ð¿Ð¾Ð¸ÑÐºÐ° Ð¸ Ñ„Ð¸Ð»ÑŒÑ‚Ñ€Ð°Ñ†Ð¸Ð¸
  const [searchQuery, setSearchQuery] = useState('');
  const [statusFilter, setStatusFilter] = useState('');
  const [sortBy, setSortBy] = useState('date_desc');
  const [isSearching, setIsSearching] = useState(false);

  useEffect(() => {
    // Ð—Ð°Ð³Ñ€ÑƒÐ¶Ð°ÐµÐ¼ Ð´Ð°Ð½Ð½Ñ‹Ðµ Ð¾ Ð¿Ð¾Ð´Ð¿Ð¸ÑÐºÐ°Ñ… Ð¿Ð¾Ð»ÑŒÐ·Ð¾Ð²Ð°Ñ‚ÐµÐ»Ñ
    fetchDashboardData();
  }, []);

  const fetchDashboardData = async () => {
    setLoading(true);
    setError('');

    try {
      // Ð—Ð°Ð³Ñ€ÑƒÐ·ÐºÐ° Ð²ÑÐµÑ… Ð¿Ð¾Ð´Ð¿Ð¸ÑÐ¾Ðº Ð¿Ð¾Ð»ÑŒÐ·Ð¾Ð²Ð°Ñ‚ÐµÐ»Ñ
      const subsData = await subscriptionsAPI.getAll();
      // Ð¤Ð¸Ð»ÑŒÑ‚Ñ€ÑƒÐµÐ¼ Ñ‚Ð¾Ð»ÑŒÐºÐ¾ ÐµÐ¶ÐµÐ¼ÐµÑÑÑ‡Ð½Ñ‹Ðµ Ð¿Ð¾Ð´Ð¿Ð¸ÑÐºÐ¸
      const allSubs = (subsData.subscriptions || []).filter(sub => {
        // ÐŸÑ€Ð¾Ð²ÐµÑ€ÑÐµÐ¼, Ñ‡Ñ‚Ð¾ ÑÑ‚Ð¾ Ð¼ÐµÑÑÑ‡Ð½Ð°Ñ Ð¿Ð¾Ð´Ð¿Ð¸ÑÐºÐ° (Ð¾ÐºÐ¾Ð»Ð¾ 30 Ð´Ð½ÐµÐ¹)
        return (sub.plan.duration >= 28 && sub.plan.duration <= 31) || 
               (sub.plan.period_type === 'months' && sub.plan.duration === 1);
      });

      setSubscriptions(allSubs);

      // Ð—Ð°Ð³Ñ€ÑƒÐ·ÐºÐ° Ñ‚Ð¾Ð»ÑŒÐºÐ¾ Ð°ÐºÑ‚Ð¸Ð²Ð½Ñ‹Ñ… Ð¿Ð¾Ð´Ð¿Ð¸ÑÐ¾Ðº
      const activeData = await subscriptionsAPI.getActive();
      // Ð¤Ð¸Ð»ÑŒÑ‚Ñ€ÑƒÐµÐ¼ Ñ‚Ð¾Ð»ÑŒÐºÐ¾ ÐµÐ¶ÐµÐ¼ÐµÑÑÑ‡Ð½Ñ‹Ðµ Ð¿Ð¾Ð´Ð¿Ð¸ÑÐºÐ¸ ÑÑ€ÐµÐ´Ð¸ Ð°ÐºÑ‚Ð¸Ð²Ð½Ñ‹Ñ…
      const activeSubs = (activeData.active_subscriptions || []).filter(sub => {
        // ÐŸÑ€Ð¾Ð²ÐµÑ€ÑÐµÐ¼, Ñ‡Ñ‚Ð¾ ÑÑ‚Ð¾ Ð¼ÐµÑÑÑ‡Ð½Ð°Ñ Ð¿Ð¾Ð´Ð¿Ð¸ÑÐºÐ° (Ð¾ÐºÐ¾Ð»Ð¾ 30 Ð´Ð½ÐµÐ¹)
        return (sub.plan.duration >= 28 && sub.plan.duration <= 31) || 
               (sub.plan.period_type === 'months' && sub.plan.duration === 1);
      });

      setActiveSubscriptions(activeSubs);

      // Ð—Ð°Ð³Ñ€ÑƒÐ·ÐºÐ° ÑÑ‚Ð°Ñ‚Ð¸ÑÑ‚Ð¸ÐºÐ¸ Ð¿Ð¾Ð´Ð¿Ð¸ÑÐ¾Ðº
      const statsData = await subscriptionsAPI.getStats();
      // Ð—Ð´ÐµÑÑŒ Ð½Ðµ Ñ„Ð¸Ð»ÑŒÑ‚Ñ€ÑƒÐµÐ¼, Ñ‚Ð°Ðº ÐºÐ°Ðº ÑÑ‚Ð°Ñ‚Ð¸ÑÑ‚Ð¸ÐºÐ° Ð¼Ð¾Ð¶ÐµÑ‚ ÑƒÐ¶Ðµ ÑƒÑ‡Ð¸Ñ‚Ñ‹Ð²Ð°Ñ‚ÑŒ Ñ‚Ð¾Ð»ÑŒÐºÐ¾ Ð¼ÐµÑÑÑ‡Ð½Ñ‹Ðµ Ð¿Ð¾Ð´Ð¿Ð¸ÑÐºÐ¸
      setStats(statsData.stats || {
        active_count: activeSubs.length,
        total_monthly_spending: activeSubs.reduce((total, sub) => total + sub.plan.price, 0)
      });
    } catch (err) {
      setError(err.message);
    } finally {
      setLoading(false);
    }
  };

  // Ð¤ÑƒÐ½ÐºÑ†Ð¸Ñ Ð´Ð»Ñ Ð¿Ð¾Ð¸ÑÐºÐ° Ð¿Ð¾Ð´Ð¿Ð¸ÑÐ¾Ðº
  const handleSearch = async (e) => {
    e.preventDefault();
    setIsSearching(true);

    try {
      const data = await subscriptionsAPI.search(searchQuery, statusFilter, sortBy);
      // Ð¤Ð¸Ð»ÑŒÑ‚Ñ€ÑƒÐµÐ¼ Ñ‚Ð¾Ð»ÑŒÐºÐ¾ ÐµÐ¶ÐµÐ¼ÐµÑÑÑ‡Ð½Ñ‹Ðµ Ð¿Ð¾Ð´Ð¿Ð¸ÑÐºÐ¸
      const filteredSubs = (data.subscriptions || []).filter(sub => {
        // ÐŸÑ€Ð¾Ð²ÐµÑ€ÑÐµÐ¼, Ñ‡Ñ‚Ð¾ ÑÑ‚Ð¾ Ð¼ÐµÑÑÑ‡Ð½Ð°Ñ Ð¿Ð¾Ð´Ð¿Ð¸ÑÐºÐ° (Ð¾ÐºÐ¾Ð»Ð¾ 30 Ð´Ð½ÐµÐ¹)
        return (sub.plan.duration >= 28 && sub.plan.duration <= 31) || 
               (sub.plan.period_type === 'months' && sub.plan.duration === 1);
      });

      setSubscriptions(filteredSubs);
    } catch (err) {
      setError(err.message);
    } finally {
      setIsSearching(false);
    }
  };

  // Ð›Ð¾ÐºÐ°Ð»ÑŒÐ½Ð°Ñ ÑÐ¾Ñ€Ñ‚Ð¸Ñ€Ð¾Ð²ÐºÐ° Ð¿Ð¾Ð´Ð¿Ð¸ÑÐ¾Ðº (Ð½Ð° ÑÐ»ÑƒÑ‡Ð°Ð¹, ÐµÑÐ»Ð¸ API Ð½Ðµ Ð¿Ð¾Ð´Ð´ÐµÑ€Ð¶Ð¸Ð²Ð°ÐµÑ‚ ÑÐ¾Ñ€Ñ‚Ð¸Ñ€Ð¾Ð²ÐºÑƒ)
  const sortSubscriptions = (subs, sortType) => {
    if (!subs || subs.length === 0) return [];

    const sorted = [...subs];

    switch (sortType) {
      case 'price_asc':
        return sorted.sort((a, b) => a.plan.price - b.plan.price);
      case 'price_desc':
        return sorted.sort((a, b) => b.plan.price - a.plan.price);
      case 'date_asc':
        return sorted.sort((a, b) => new Date(a.start_date) - new Date(b.start_date));
      case 'date_desc':
        return sorted.sort((a, b) => new Date(b.start_date) - new Date(a.start_date));
      case 'name_asc':
        return sorted.sort((a, b) => a.plan.name.localeCompare(b.plan.name));
      case 'name_desc':
        return sorted.sort((a, b) => b.plan.name.localeCompare(a.plan.name));
      default:
        return sorted;
    }
  };

  // ÐŸÑ€Ð¸Ð¼ÐµÐ½Ð¸Ñ‚ÑŒ ÑÐ¾Ñ€Ñ‚Ð¸Ñ€Ð¾Ð²ÐºÑƒ Ðº Ñ‚ÐµÐºÑƒÑ‰ÐµÐ¼Ñƒ ÑÐ¿Ð¸ÑÐºÑƒ
  const handleSort = (sortType) => {
    setSortBy(sortType);
    setSubscriptions(sortSubscriptions(subscriptions, sortType));
    setActiveSubscriptions(sortSubscriptions(activeSubscriptions, sortType));
  };

  // ÐŸÐ¾Ð¸ÑÐº Ð¿Ð¾ Ð¸Ð¼ÐµÐ½Ð¸ Ð»Ð¾ÐºÐ°Ð»ÑŒÐ½Ð¾
  const handleLocalSearch = (query) => {
    setSearchQuery(query);

    if (!query.trim() && !statusFilter) {
      // Ð•ÑÐ»Ð¸ Ð¿Ð¾Ð¸ÑÐº Ð¿ÑƒÑÑ‚Ð¾Ð¹ Ð¸ Ð½ÐµÑ‚ Ñ„Ð¸Ð»ÑŒÑ‚Ñ€Ð° Ð¿Ð¾ ÑÑ‚Ð°Ñ‚ÑƒÑÑƒ, Ð²Ð¾ÑÑÑ‚Ð°Ð½Ð¾Ð²Ð¸Ð¼ Ð¾Ñ€Ð¸Ð³Ð¸Ð½Ð°Ð»ÑŒÐ½Ñ‹Ð¹ ÑÐ¿Ð¸ÑÐ¾Ðº
      fetchDashboardData();
      return;
    }

    // ÐŸÐ¾Ð»ÑƒÑ‡Ð°ÐµÐ¼ Ð¾Ñ€Ð¸Ð³Ð¸Ð½Ð°Ð»ÑŒÐ½Ñ‹Ðµ Ð´Ð°Ð½Ð½Ñ‹Ðµ Ð¸Ð· API Ð¸ Ñ„Ð¸Ð»ÑŒÑ‚Ñ€ÑƒÐµÐ¼ Ð»Ð¾ÐºÐ°Ð»ÑŒÐ½Ð¾
    subscriptionsAPI.getAll().then(data => {
      let filteredSubs = (data.subscriptions || []);

      // Ð¤Ð¸Ð»ÑŒÑ‚Ñ€ÑƒÐµÐ¼ Ñ‚Ð¾Ð»ÑŒÐºÐ¾ ÐµÐ¶ÐµÐ¼ÐµÑÑÑ‡Ð½Ñ‹Ðµ Ð¿Ð¾Ð´Ð¿Ð¸ÑÐºÐ¸
      filteredSubs = filteredSubs.filter(sub => 
        (sub.plan.duration >= 28 && sub.plan.duration <= 31) || 
        (sub.plan.period_type === 'months' && sub.plan.duration === 1)
      );

      // ÐŸÑ€Ð¸Ð¼ÐµÐ½ÑÐµÐ¼ Ð¿Ð¾Ð¸ÑÐº Ð¿Ð¾ Ð¸Ð¼ÐµÐ½Ð¸
      if (query.trim()) {
        filteredSubs = filteredSubs.filter(sub => 
          sub.plan.name.toLowerCase().includes(query.toLowerCase())
        );
      }

      // ÐŸÑ€Ð¸Ð¼ÐµÐ½ÑÐµÐ¼ Ñ„Ð¸Ð»ÑŒÑ‚Ñ€ Ð¿Ð¾ ÑÑ‚Ð°Ñ‚ÑƒÑÑƒ
      if (statusFilter) {
        filteredSubs = filteredSubs.filter(sub => 
          sub.status === statusFilter
        );
      }

      // ÐŸÑ€Ð¸Ð¼ÐµÐ½ÑÐµÐ¼ Ñ‚ÐµÐºÑƒÑ‰ÑƒÑŽ ÑÐ¾Ñ€Ñ‚Ð¸Ñ€Ð¾Ð²ÐºÑƒ
      filteredSubs = sortSubscriptions(filteredSubs, sortBy);

      setSubscriptions(filteredSubs);

      // ÐžÐ±Ð½Ð¾Ð²Ð»ÑÐµÐ¼ Ð°ÐºÑ‚Ð¸Ð²Ð½Ñ‹Ðµ Ð¿Ð¾Ð´Ð¿Ð¸ÑÐºÐ¸ Ñ ÑƒÑ‡ÐµÑ‚Ð¾Ð¼ Ñ„Ð¸Ð»ÑŒÑ‚Ñ€Ð°Ñ†Ð¸Ð¸
      const activeSubs = filteredSubs.filter(sub => sub.status === 'active');
      setActiveSubscriptions(activeSubs);
    }).catch(err => {
      setError(err.message);
    });
  };

  // Ð¡Ð±Ñ€Ð¾Ñ Ð¿Ð¾Ð¸ÑÐºÐ°
  const resetSearch = () => {
    setSearchQuery('');
    setStatusFilter('');
    setSortBy('date_desc');
    fetchDashboardData();
  };

  const handleCancelSubscription = async (subscriptionId) => {
    if (!window.confirm('Ð’Ñ‹ ÑƒÐ²ÐµÑ€ÐµÐ½Ñ‹, Ñ‡Ñ‚Ð¾ Ñ…Ð¾Ñ‚Ð¸Ñ‚Ðµ Ð¾Ñ‚Ð¼ÐµÐ½Ð¸Ñ‚ÑŒ Ð¿Ð¾Ð´Ð¿Ð¸ÑÐºÑƒ?')) {
      return;
    }

    setError('');
    setActionSuccess('');

    try {
      const data = await subscriptionsAPI.cancel(subscriptionId);
      setActionSuccess('ÐŸÐ¾Ð´Ð¿Ð¸ÑÐºÐ° ÑƒÑÐ¿ÐµÑˆÐ½Ð¾ Ð¾Ñ‚Ð¼ÐµÐ½ÐµÐ½Ð°');

      // ÐžÐ±Ð½Ð¾Ð²Ð»ÑÐµÐ¼ ÑÐ¿Ð¸ÑÐ¾Ðº Ð¿Ð¾Ð´Ð¿Ð¸ÑÐ¾Ðº
      fetchDashboardData();
    } catch (err) {
      setError(err.message);
    }
  };

  const handleToggleAutoRenewal = async (subscriptionId, currentAutoRenew) => {
    setError('');
    setActionSuccess('');

    try {
      const data = await subscriptionsAPI.updateAutoRenewal(subscriptionId, !currentAutoRenew);
      setActionSuccess(`ÐÐ²Ñ‚Ð¾Ð¿Ñ€Ð¾Ð´Ð»ÐµÐ½Ð¸Ðµ ${!currentAutoRenew ? 'Ð²ÐºÐ»ÑŽÑ‡ÐµÐ½Ð¾' : 'Ð¾Ñ‚ÐºÐ»ÑŽÑ‡ÐµÐ½Ð¾'} ÑƒÑÐ¿ÐµÑˆÐ½Ð¾`);

      // ÐžÐ±Ð½Ð¾Ð²Ð»ÑÐµÐ¼ ÑÐ¿Ð¸ÑÐ¾Ðº Ð¿Ð¾Ð´Ð¿Ð¸ÑÐ¾Ðº
      fetchDashboardData();
    } catch (err) {
      setError(err.message);
    }
  };

  // Ð¤ÑƒÐ½ÐºÑ†Ð¸Ñ Ð´Ð»Ñ Ð¿Ñ€Ð¾Ð´Ð»ÐµÐ½Ð¸Ñ Ð¿Ð¾Ð´Ð¿Ð¸ÑÐºÐ¸
  const handleRenewSubscription = async (subscriptionId) => {
    if (!window.confirm('Ð’Ñ‹ Ñ…Ð¾Ñ‚Ð¸Ñ‚Ðµ Ð¿Ñ€Ð¾Ð´Ð»Ð¸Ñ‚ÑŒ ÑÑ‚Ñƒ Ð¿Ð¾Ð´Ð¿Ð¸ÑÐºÑƒ?')) {
      return;
    }

    setError('');
    setActionSuccess('');

    try {
      const data = await subscriptionsAPI.renew(subscriptionId);
      setActionSuccess('ÐŸÐ¾Ð´Ð¿Ð¸ÑÐºÐ° ÑƒÑÐ¿ÐµÑˆÐ½Ð¾ Ð¿Ñ€Ð¾Ð´Ð»ÐµÐ½Ð°');

      // ÐžÐ±Ð½Ð¾Ð²Ð»ÑÐµÐ¼ ÑÐ¿Ð¸ÑÐ¾Ðº Ð¿Ð¾Ð´Ð¿Ð¸ÑÐ¾Ðº
      fetchDashboardData();
    } catch (err) {
      setError(err.message);
    }
  };

  const formatDate = (dateString) => {
    const date = new Date(dateString);
    return date.toLocaleDateString('ru-RU', {
      day: '2-digit',
      month: '2-digit',
      year: 'numeric'
    });
  };

  // ÐŸÐ¾Ð»ÑƒÑ‡ÐµÐ½Ð¸Ðµ ÑÑ‚Ð°Ñ‚ÑƒÑÐ° Ð¿Ð¾Ð´Ð¿Ð¸ÑÐºÐ¸ Ð² Ñ€ÑƒÑÑÐºÐ¾Ð¼ Ð²Ð°Ñ€Ð¸Ð°Ð½Ñ‚Ðµ
  const getStatusInRussian = (status) => {
    switch(status) {
      case 'active': return 'ÐÐºÑ‚Ð¸Ð²Ð½Ð°';
      case 'cancelled': return 'ÐžÑ‚Ð¼ÐµÐ½ÐµÐ½Ð°';
      case 'expired': return 'Ð˜ÑÑ‚ÐµÐºÐ»Ð°';
      default: return status;
    }
  };

  // ÐŸÐ¾Ð»ÑƒÑ‡ÐµÐ½Ð¸Ðµ Ñ†Ð²ÐµÑ‚Ð° Ð´Ð»Ñ ÑÑ‚Ð°Ñ‚ÑƒÑÐ°
  const getStatusColor = (status) => {
    switch(status) {
      case 'active': return 'success';
      case 'cancelled': return 'warning';
      case 'expired': return 'secondary';
      default: return 'info';
    }
  };

  // ÐŸÐ¾Ð»ÑƒÑ‡ÐµÐ½Ð¸Ðµ Ð¸ÐºÐ¾Ð½ÐºÐ¸ Ð´Ð»Ñ ÑÐµÑ€Ð²Ð¸ÑÐ°
  const getServiceIcon = (serviceName) => {
    if (!serviceName) return SERVICE_ICONS.default;

    const name = serviceName.toLowerCase();

    for (const [key, icon] of Object.entries(SERVICE_ICONS)) {
      if (key !== 'default' && name.includes(key)) {
        return icon;
      }
    }

    return SERVICE_ICONS.default;
  };

  // ÐžÐ¿Ñ€ÐµÐ´ÐµÐ»ÐµÐ½Ð¸Ðµ Ð½Ð°Ð·Ð²Ð°Ð½Ð¸Ñ Ð´Ð»Ñ ÑÐ¾Ñ€Ñ‚Ð¸Ñ€Ð¾Ð²ÐºÐ¸
  const getSortName = (sortValue) => {
    switch(sortValue) {
      case 'price_asc': return 'Ð¦ÐµÐ½Ð° (Ð¿Ð¾ Ð²Ð¾Ð·Ñ€Ð°ÑÑ‚Ð°Ð½Ð¸ÑŽ)';
      case 'price_desc': return 'Ð¦ÐµÐ½Ð° (Ð¿Ð¾ ÑƒÐ±Ñ‹Ð²Ð°Ð½Ð¸ÑŽ)';
      case 'date_asc': return 'Ð”Ð°Ñ‚Ð° (ÑÑ‚Ð°Ñ€Ñ‹Ðµ ÑÐ½Ð°Ñ‡Ð°Ð»Ð°)';
      case 'date_desc': return 'Ð”Ð°Ñ‚Ð° (Ð½Ð¾Ð²Ñ‹Ðµ ÑÐ½Ð°Ñ‡Ð°Ð»Ð°)';
      case 'name_asc': return 'ÐÐ°Ð·Ð²Ð°Ð½Ð¸Ðµ (Ð-Ð¯)';
      case 'name_desc': return 'ÐÐ°Ð·Ð²Ð°Ð½Ð¸Ðµ (Ð¯-Ð)';
      default: return 'ÐŸÐ¾ ÑƒÐ¼Ð¾Ð»Ñ‡Ð°Ð½Ð¸ÑŽ';
    }
  };

  return (
    <Container className="py-5">
      <h1 className="mb-4">Ð£Ð¿Ñ€Ð°Ð²Ð»ÐµÐ½Ð¸Ðµ Ð¿Ð¾Ð´Ð¿Ð¸ÑÐºÐ°Ð¼Ð¸</h1>

      {error && <Alert variant="danger" dismissible onClose={() => setError('')}>
        <Alert.Heading>ÐžÑˆÐ¸Ð±ÐºÐ°!</Alert.Heading>
        <p>{error}</p>
      </Alert>}

      {actionSuccess && <Alert variant="success" dismissible onClose={() => setActionSuccess('')}>
        <Alert.Heading>Ð£ÑÐ¿ÐµÑˆÐ½Ð¾!</Alert.Heading>
        <p>{actionSuccess}</p>
      </Alert>}

      {loading ? (
        <div className="text-center my-5">
          <div className="spinner-border" role="status">
            <span className="visually-hidden">Ð—Ð°Ð³Ñ€ÑƒÐ·ÐºÐ°...</span>
          </div>
        </div>
      ) : (
        <>
          <Row className="mb-4">
            <Col md={6} lg={3} className="mb-3">
              <Card className="subscription-stat-card h-100">
                <Card.Body className="text-center">
                  <h6>ÐÐºÑ‚Ð¸Ð²Ð½Ñ‹Ðµ Ð¿Ð¾Ð´Ð¿Ð¸ÑÐºÐ¸</h6>
                  <div className="display-4">{stats.active_count}</div>
                </Card.Body>
              </Card>
            </Col>
            <Col md={6} lg={3} className="mb-3">
              <Card className="subscription-stat-card h-100">
                <Card.Body className="text-center">
                  <h6>Ð•Ð¶ÐµÐ¼ÐµÑÑÑ‡Ð½Ñ‹Ðµ Ñ€Ð°ÑÑ…Ð¾Ð´Ñ‹</h6>
                  <div className="display-4">{stats.total_monthly_spending ? stats.total_monthly_spending.toFixed(2) : 0} â‚½</div>
                </Card.Body>
              </Card>
            </Col>
            <Col lg={6} className="mb-3 d-flex align-items-center justify-content-end">
              <Button as={Link} to="/plans" variant="primary">
                Ð”Ð¾Ð±Ð°Ð²Ð¸Ñ‚ÑŒ Ð½Ð¾Ð²ÑƒÑŽ Ð¿Ð¾Ð´Ð¿Ð¸ÑÐºÑƒ
              </Button>
            </Col>
          </Row>

          {/* ÐŸÐ°Ð½ÐµÐ»ÑŒ Ð¿Ð¾Ð¸ÑÐºÐ° Ð¸ Ñ„Ð¸Ð»ÑŒÑ‚Ñ€Ð°Ñ†Ð¸Ð¸ */}
          <Row className="mb-4">
            <Col xs={12}>
              <Card>
                <Card.Body>
                  <Form>
                    <Row className="align-items-end">
                      <Col md={4} className="mb-3 mb-md-0">
                        <Form.Group>
                          <Form.Label>ÐŸÐ¾Ð¸ÑÐº Ð¿Ð¾ Ð½Ð°Ð·Ð²Ð°Ð½Ð¸ÑŽ</Form.Label>
                          <InputGroup>
                            <Form.Control
                              type="text"
                              value={searchQuery}
                              onChange={e => handleLocalSearch(e.target.value)}
                              placeholder="ÐÐ°Ð¿Ñ€Ð¸Ð¼ÐµÑ€: Netflix, Ð¯Ð½Ð´ÐµÐºÑ..."
                            />
                            <Button variant="outline-secondary" onClick={() => setSearchQuery('')}>
                              {searchQuery ? <XCircle /> : <Search />}
                            </Button>
                          </InputGroup>
                        </Form.Group>
                      </Col>
                      <Col md={3} className="mb-3 mb-md-0">
                        <Form.Group>
                          <Form.Label>Ð¡Ñ‚Ð°Ñ‚ÑƒÑ</Form.Label>
                          <Form.Select 
                            value={statusFilter}
                            onChange={e => {
                              setStatusFilter(e.target.value);
                              handleLocalSearch(searchQuery); // ÐŸÑ€Ð¸Ð¼ÐµÐ½ÑÐµÐ¼ Ð¸Ð·Ð¼ÐµÐ½ÐµÐ½Ð¸Ðµ Ñ„Ð¸Ð»ÑŒÑ‚Ñ€Ð°
                            }}
                          >
                            <option value="">Ð’ÑÐµ ÑÑ‚Ð°Ñ‚ÑƒÑÑ‹</option>
                            <option value="active">ÐÐºÑ‚Ð¸Ð²Ð½Ñ‹Ðµ</option>
                            <option value="cancelled">ÐžÑ‚Ð¼ÐµÐ½Ñ‘Ð½Ð½Ñ‹Ðµ</option>
                            <option value="expired">Ð˜ÑÑ‚ÐµÐºÑˆÐ¸Ðµ</option>
                          </Form.Select>
                        </Form.Group>
                      </Col>
                      <Col md={3} className="mb-3 mb-md-0">
                        <Form.Group>
                          <Form.Label>Ð¡Ð¾Ñ€Ñ‚Ð¸Ñ€Ð¾Ð²ÐºÐ°</Form.Label>
                          <Dropdown>
                            <Dropdown.Toggle variant="outline-secondary" id="dropdown-sort" className="w-100 text-start">
                              <ArrowDownUp className="me-2" />
                              {getSortName(sortBy)}
