import React, { useState, useEffect } from 'react';
import { Container, Row, Col, Card, Button, Alert, Badge, Tabs, Tab, Form, InputGroup, Dropdown } from 'react-bootstrap';
import { Link } from 'react-router-dom';
import { Search, XCircle, SortDown, ArrowDownUp } from 'react-bootstrap-icons';
import { subscriptionsAPI } from '../utils/api';

// –°–ª–æ–≤–∞—Ä—å –∏–∫–æ–Ω–æ–∫ –¥–ª—è —Ä–∞–∑–Ω—ã—Ö —Å–µ—Ä–≤–∏—Å–æ–≤ –ø–æ–¥–ø–∏—Å–æ–∫
const SERVICE_ICONS = {
  '—è–Ω–¥–µ–∫—Å': 'üîç',
  'netflix': 'üì∫',
  'spotify': 'üéµ',
  'apple': 'üçé',
  'google': '‚òÅÔ∏è',
  '–∫–∏–Ω–æ–ø–æ–∏—Å–∫': 'üé¨',
  '–∞–º–µ–¥–∏–∞—Ç–µ–∫–∞': 'üìΩÔ∏è',
  'amediateka': 'üìΩÔ∏è',
  'ivi': 'üé¶',
  'premier': 'üéûÔ∏è',
  'default': 'üì±' // –∏–∫–æ–Ω–∫–∞ –ø–æ —É–º–æ–ª—á–∞–Ω–∏—é
};

const Dashboard = ({ user }) => {
  const [subscriptions, setSubscriptions] = useState([]);
  const [activeSubscriptions, setActiveSubscriptions] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [actionSuccess, setActionSuccess] = useState('');
  const [stats, setStats] = useState({
    active_count: 0,
    total_monthly_spending: 0
  });

  // –°–æ—Å—Ç–æ—è–Ω–∏—è –¥–ª—è –ø–æ–∏—Å–∫–∞ –∏ —Ñ–∏–ª—å—Ç—Ä–∞—Ü–∏–∏
  const [searchQuery, setSearchQuery] = useState('');
  const [statusFilter, setStatusFilter] = useState('');
  const [sortBy, setSortBy] = useState('date_desc');
  const [isSearching, setIsSearching] = useState(false);

  useEffect(() => {
    // –ó–∞–≥—Ä—É–∂–∞–µ–º –¥–∞–Ω–Ω—ã–µ –æ –ø–æ–¥–ø–∏—Å–∫–∞—Ö –ø–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª—è
    fetchDashboardData();
  }, []);

  const fetchDashboardData = async () => {
    setLoading(true);
    setError('');

    try {
      // –ó–∞–≥—Ä—É–∑–∫–∞ –≤—Å–µ—Ö –ø–æ–¥–ø–∏—Å–æ–∫ –ø–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª—è
      const subsData = await subscriptionsAPI.getAll();
      // –§–∏–ª—å—Ç—Ä—É–µ–º —Ç–æ–ª—å–∫–æ –µ–∂–µ–º–µ—Å—è—á–Ω—ã–µ –ø–æ–¥–ø–∏—Å–∫–∏
      const allSubs = (subsData.subscriptions || []).filter(sub => {
        // –ü—Ä–æ–≤–µ—Ä—è–µ–º, —á—Ç–æ —ç—Ç–æ –º–µ—Å—è—á–Ω–∞—è –ø–æ–¥–ø–∏—Å–∫–∞ (–æ–∫–æ–ª–æ 30 –¥–Ω–µ–π)
        return (sub.plan.duration >= 28 && sub.plan.duration <= 31) || 
               (sub.plan.period_type === 'months' && sub.plan.duration === 1);
      });

      setSubscriptions(allSubs);

      // –ó–∞–≥—Ä—É–∑–∫–∞ —Ç–æ–ª—å–∫–æ –∞–∫—Ç–∏–≤–Ω—ã—Ö –ø–æ–¥–ø–∏—Å–æ–∫
      const activeData = await subscriptionsAPI.getActive();
      // –§–∏–ª—å—Ç—Ä—É–µ–º —Ç–æ–ª—å–∫–æ –µ–∂–µ–º–µ—Å—è—á–Ω—ã–µ –ø–æ–¥–ø–∏—Å–∫–∏ —Å—Ä–µ–¥–∏ –∞–∫—Ç–∏–≤–Ω—ã—Ö
      const activeSubs = (activeData.active_subscriptions || []).filter(sub => {
        // –ü—Ä–æ–≤–µ—Ä—è–µ–º, —á—Ç–æ —ç—Ç–æ –º–µ—Å—è—á–Ω–∞—è –ø–æ–¥–ø–∏—Å–∫–∞ (–æ–∫–æ–ª–æ 30 –¥–Ω–µ–π)
        return (sub.plan.duration >= 28 && sub.plan.duration <= 31) || 
               (sub.plan.period_type === 'months' && sub.plan.duration === 1);
      });

      setActiveSubscriptions(activeSubs);

      // –ó–∞–≥—Ä—É–∑–∫–∞ —Å—Ç–∞—Ç–∏—Å—Ç–∏–∫–∏ –ø–æ–¥–ø–∏—Å–æ–∫
      const statsData = await subscriptionsAPI.getStats();
      // –ó–¥–µ—Å—å –Ω–µ —Ñ–∏–ª—å—Ç—Ä—É–µ–º, —Ç–∞–∫ –∫–∞–∫ —Å—Ç–∞—Ç–∏—Å—Ç–∏–∫–∞ –º–æ–∂–µ—Ç —É–∂–µ —É—á–∏—Ç—ã–≤–∞—Ç—å —Ç–æ–ª—å–∫–æ –º–µ—Å—è—á–Ω—ã–µ –ø–æ–¥–ø–∏—Å–∫–∏
      setStats(statsData.stats || {
        active_count: activeSubs.length,
        total_monthly_spending: activeSubs.reduce((total, sub) => total + sub.plan.price, 0)
      });
    } catch (err) {
      setError(err.message);
    } finally {
      setLoading(false);
    }
  };

  // –§—É–Ω–∫—Ü–∏—è –¥–ª—è –ø–æ–∏—Å–∫–∞ –ø–æ–¥–ø–∏—Å–æ–∫
  const handleSearch = async (e) => {
    e.preventDefault();
    setIsSearching(true);

    try {
      const data = await subscriptionsAPI.search(searchQuery, statusFilter, sortBy);
      // –§–∏–ª—å—Ç—Ä—É–µ–º —Ç–æ–ª—å–∫–æ –µ–∂–µ–º–µ—Å—è—á–Ω—ã–µ –ø–æ–¥–ø–∏—Å–∫–∏
      const filteredSubs = (data.subscriptions || []).filter(sub => {
        // –ü—Ä–æ–≤–µ—Ä—è–µ–º, —á—Ç–æ —ç—Ç–æ –º–µ—Å—è—á–Ω–∞—è –ø–æ–¥–ø–∏—Å–∫–∞ (–æ–∫–æ–ª–æ 30 –¥–Ω–µ–π)
        return (sub.plan.duration >= 28 && sub.plan.duration <= 31) || 
               (sub.plan.period_type === 'months' && sub.plan.duration === 1);
      });

      setSubscriptions(filteredSubs);
    } catch (err) {
      setError(err.message);
    } finally {
      setIsSearching(false);
    }
  };

  // –õ–æ–∫–∞–ª—å–Ω–∞—è —Å–æ—Ä—Ç–∏—Ä–æ–≤–∫–∞ –ø–æ–¥–ø–∏—Å–æ–∫ (–Ω–∞ —Å–ª—É—á–∞–π, –µ—Å–ª–∏ API –Ω–µ –ø–æ–¥–¥–µ—Ä–∂–∏–≤–∞–µ—Ç —Å–æ—Ä—Ç–∏—Ä–æ–≤–∫—É)
  const sortSubscriptions = (subs, sortType) => {
    if (!subs || subs.length === 0) return [];

    const sorted = [...subs];

    switch (sortType) {
      case 'price_asc':
        return sorted.sort((a, b) => a.plan.price - b.plan.price);
      case 'price_desc':
        return sorted.sort((a, b) => b.plan.price - a.plan.price);
      case 'date_asc':
        return sorted.sort((a, b) => new Date(a.start_date) - new Date(b.start_date));
      case 'date_desc':
        return sorted.sort((a, b) => new Date(b.start_date) - new Date(a.start_date));
      case 'name_asc':
        return sorted.sort((a, b) => a.plan.name.localeCompare(b.plan.name));
      case 'name_desc':
        return sorted.sort((a, b) => b.plan.name.localeCompare(a.plan.name));
      default:
        return sorted;
    }
  };

  // –ü—Ä–∏–º–µ–Ω–∏—Ç—å —Å–æ—Ä—Ç–∏—Ä–æ–≤–∫—É –∫ —Ç–µ–∫—É—â–µ–º—É —Å–ø–∏—Å–∫—É
  const handleSort = (sortType) => {
    setSortBy(sortType);
    setSubscriptions(sortSubscriptions(subscriptions, sortType));
    setActiveSubscriptions(sortSubscriptions(activeSubscriptions, sortType));
  };

  // –ü–æ–∏—Å–∫ –ø–æ –∏–º–µ–Ω–∏ –ª–æ–∫–∞–ª—å–Ω–æ
  const handleLocalSearch = (query) => {
    setSearchQuery(query);

    if (!query.trim() && !statusFilter) {
      // –ï—Å–ª–∏ –ø–æ–∏—Å–∫ –ø—É—Å—Ç–æ–π –∏ –Ω–µ—Ç —Ñ–∏–ª—å—Ç—Ä–∞ –ø–æ —Å—Ç–∞—Ç—É—Å—É, –≤–æ—Å—Å—Ç–∞–Ω–æ–≤–∏–º –æ—Ä–∏–≥–∏–Ω–∞–ª—å–Ω—ã–π —Å–ø–∏—Å–æ–∫
      fetchDashboardData();
      return;
    }

    // –ü–æ–ª—É—á–∞–µ–º –æ—Ä–∏–≥–∏–Ω–∞–ª—å–Ω—ã–µ –¥–∞–Ω–Ω—ã–µ –∏–∑ API –∏ —Ñ–∏–ª—å—Ç—Ä—É–µ–º –ª–æ–∫–∞–ª—å–Ω–æ
    subscriptionsAPI.getAll().then(data => {
      let filteredSubs = (data.subscriptions || []);

      // –§–∏–ª—å—Ç—Ä—É–µ–º —Ç–æ–ª—å–∫–æ –µ–∂–µ–º–µ—Å—è—á–Ω—ã–µ –ø–æ–¥–ø–∏—Å–∫–∏
      filteredSubs = filteredSubs.filter(sub => 
        (sub.plan.duration >= 28 && sub.plan.duration <= 31) || 
        (sub.plan.period_type === 'months' && sub.plan.duration === 1)
      );

      // –ü—Ä–∏–º–µ–Ω—è–µ–º –ø–æ–∏—Å–∫ –ø–æ –∏–º–µ–Ω–∏
      if (query.trim()) {
        filteredSubs = filteredSubs.filter(sub => 
          sub.plan.name.toLowerCase().includes(query.toLowerCase())
        );
      }

      // –ü—Ä–∏–º–µ–Ω—è–µ–º —Ñ–∏–ª—å—Ç—Ä –ø–æ —Å—Ç–∞—Ç—É—Å—É
      if (statusFilter) {
        filteredSubs = filteredSubs.filter(sub => 
          sub.status === statusFilter
        );
      }

      // –ü—Ä–∏–º–µ–Ω—è–µ–º —Ç–µ–∫—É—â—É—é —Å–æ—Ä—Ç–∏—Ä–æ–≤–∫—É
      filteredSubs = sortSubscriptions(filteredSubs, sortBy);

      setSubscriptions(filteredSubs);

      // –û–±–Ω–æ–≤–ª—è–µ–º –∞–∫—Ç–∏–≤–Ω—ã–µ –ø–æ–¥–ø–∏—Å–∫–∏ —Å —É—á–µ—Ç–æ–º —Ñ–∏–ª—å—Ç—Ä–∞—Ü–∏–∏
      const activeSubs = filteredSubs.filter(sub => sub.status === 'active');
      setActiveSubscriptions(activeSubs);
    }).catch(err => {
      setError(err.message);
    });
  };

  // –°–±—Ä–æ—Å –ø–æ–∏—Å–∫–∞
  const resetSearch = () => {
    setSearchQuery('');
    setStatusFilter('');
    setSortBy('date_desc');
    fetchDashboardData();
  };

  const handleCancelSubscription = async (subscriptionId) => {
    if (!window.confirm('–í—ã —É–≤–µ—Ä–µ–Ω—ã, —á—Ç–æ —Ö–æ—Ç–∏—Ç–µ –æ—Ç–º–µ–Ω–∏—Ç—å –ø–æ–¥–ø–∏—Å–∫—É?')) {
      return;
    }

    setError('');
    setActionSuccess('');

    try {
      const data = await subscriptionsAPI.cancel(subscriptionId);
      setActionSuccess('–ü–æ–¥–ø–∏—Å–∫–∞ —É—Å–ø–µ—à–Ω–æ –æ—Ç–º–µ–Ω–µ–Ω–∞');

      // –û–±–Ω–æ–≤–ª—è–µ–º —Å–ø–∏—Å–æ–∫ –ø–æ–¥–ø–∏—Å–æ–∫
      fetchDashboardData();
    } catch (err) {
      setError(err.message);
    }
  };

  const handleToggleAutoRenewal = async (subscriptionId, currentAutoRenew) => {
    setError('');
    setActionSuccess('');

    try {
      const data = await subscriptionsAPI.updateAutoRenewal(subscriptionId, !currentAutoRenew);
      setActionSuccess(`–ê–≤—Ç–æ–ø—Ä–æ–¥–ª–µ–Ω–∏–µ ${!currentAutoRenew ? '–≤–∫–ª—é—á–µ–Ω–æ' : '–æ—Ç–∫–ª—é—á–µ–Ω–æ'} —É—Å–ø–µ—à–Ω–æ`);

      // –û–±–Ω–æ–≤–ª—è–µ–º —Å–ø–∏—Å–æ–∫ –ø–æ–¥–ø–∏—Å–æ–∫
      fetchDashboardData();
    } catch (err) {
      setError(err.message);
    }
  };

  // –§—É–Ω–∫—Ü–∏—è –¥–ª—è –ø—Ä–æ–¥–ª–µ–Ω–∏—è –ø–æ–¥–ø–∏—Å–∫–∏
  const handleRenewSubscription = async (subscriptionId) => {
    if (!window.confirm('–í—ã —Ö–æ—Ç–∏—Ç–µ –ø—Ä–æ–¥–ª–∏—Ç—å —ç—Ç—É –ø–æ–¥–ø–∏—Å–∫—É?')) {
      return;
    }

    setError('');
    setActionSuccess('');

    try {
      const data = await subscriptionsAPI.renew(subscriptionId);
      setActionSuccess('–ü–æ–¥–ø–∏—Å–∫–∞ —É—Å–ø–µ—à–Ω–æ –ø—Ä–æ–¥–ª–µ–Ω–∞');

      // –û–±–Ω–æ–≤–ª—è–µ–º —Å–ø–∏—Å–æ–∫ –ø–æ–¥–ø–∏—Å–æ–∫
      fetchDashboardData();
    } catch (err) {
      setError(err.message);
    }
  };

  const formatDate = (dateString) => {
    const date = new Date(dateString);
    return date.toLocaleDateString('ru-RU', {
      day: '2-digit',
      month: '2-digit',
      year: 'numeric'
    });
  };

  // –ü–æ–ª—É—á–µ–Ω–∏–µ —Å—Ç–∞—Ç—É—Å–∞ –ø–æ–¥–ø–∏—Å–∫–∏ –≤ —Ä—É—Å—Å–∫–æ–º –≤–∞—Ä–∏–∞–Ω—Ç–µ
  const getStatusInRussian = (status) => {
    switch(status) {
      case 'active': return '–ê–∫—Ç–∏–≤–Ω–∞';
      case 'cancelled': return '–û—Ç–º–µ–Ω–µ–Ω–∞';
      case 'expired': return '–ò—Å—Ç–µ–∫–ª–∞';
      default: return status;
    }
  };

  // –ü–æ–ª—É—á–µ–Ω–∏–µ —Ü–≤–µ—Ç–∞ –¥–ª—è —Å—Ç–∞—Ç—É—Å–∞
  const getStatusColor = (status) => {
    switch(status) {
      case 'active': return 'success';
      case 'cancelled': return 'warning';
      case 'expired': return 'secondary';
      default: return 'info';
    }
  };

  // –ü–æ–ª—É—á–µ–Ω–∏–µ –∏–∫–æ–Ω–∫–∏ –¥–ª—è —Å–µ—Ä–≤–∏—Å–∞
  const getServiceIcon = (serviceName) => {
    if (!serviceName) return SERVICE_ICONS.default;

    const name = serviceName.toLowerCase();

    for (const [key, icon] of Object.entries(SERVICE_ICONS)) {
      if (key !== 'default' && name.includes(key)) {
        return icon;
      }
    }

    return SERVICE_ICONS.default;
  };

  // –û–ø—Ä–µ–¥–µ–ª–µ–Ω–∏–µ –Ω–∞–∑–≤–∞–Ω–∏—è –¥–ª—è —Å–æ—Ä—Ç–∏—Ä–æ–≤–∫–∏
  const getSortName = (sortValue) => {
    switch(sortValue) {
      case 'price_asc': return '–¶–µ–Ω–∞ (–ø–æ –≤–æ–∑—Ä–∞—Å—Ç–∞–Ω–∏—é)';
      case 'price_desc': return '–¶–µ–Ω–∞ (–ø–æ —É–±—ã–≤–∞–Ω–∏—é)';
      case 'date_asc': return '–î–∞—Ç–∞ (—Å—Ç–∞—Ä—ã–µ —Å–Ω–∞—á–∞–ª–∞)';
      case 'date_desc': return '–î–∞—Ç–∞ (–Ω–æ–≤—ã–µ —Å–Ω–∞—á–∞–ª–∞)';
      case 'name_asc': return '–ù–∞–∑–≤–∞–Ω–∏–µ (–ê-–Ø)';
      case 'name_desc': return '–ù–∞–∑–≤–∞–Ω–∏–µ (–Ø-–ê)';
      default: return '–ü–æ —É–º–æ–ª—á–∞–Ω–∏—é';
    }
  };

  return (
    <Container className="py-5">
      <h1 className="mb-4">–£–ø—Ä–∞–≤–ª–µ–Ω–∏–µ –ø–æ–¥–ø–∏—Å–∫–∞–º–∏</h1>

      {error && <Alert variant="danger" dismissible onClose={() => setError('')}>
        <Alert.Heading>–û—à–∏–±–∫–∞!</Alert.Heading>
