          {loading ? (
            <div className="text-center">
              <div className="spinner-border" role="status">
                <span className="visually-hidden">–ó–∞–≥—Ä—É–∑–∫–∞...</span>
              </div>
            </div>
          ) : popularPlans.length > 0 ? (
            popularPlans.map(plan => (
              <Col md={4} key={plan.id} className="mb-4">
                <Card className="plan-card h-100">
                  <Card.Header as="h5" className="d-flex justify-content-between align-items-center">
                    {plan.name}
                    {plan.is_popular && (
                      <Badge bg="warning" text="dark">–ü–æ–ø—É–ª—è—Ä–Ω—ã–π</Badge>
                    )}
                  </Card.Header>
                  <Card.Body className="d-flex flex-column">
                    <Card.Title className="mb-3">
                      <span className="display-6">{plan.price.toFixed(2)} ‚ÇΩ</span>
                      <small className="text-muted"> / –º–µ—Å—è—Ü</small>
                    </Card.Title>
                    <Card.Text>{plan.description}</Card.Text>
                    <div className="mt-auto">
                      <Button as={Link} to="/plans" variant="outline-primary">–ü–æ–¥—Ä–æ–±–Ω–µ–µ</Button>
                    </div>
                  </Card.Body>
                </Card>
              </Col>
            ))
          ) : (
            <p className="text-center">–°–µ–π—á–∞—Å –Ω–µ—Ç –¥–æ—Å—Ç—É–ø–Ω—ã—Ö –ø–ª–∞–Ω–æ–≤.</p>
          )}

          <div className="text-center mt-4">
            <Button as={Link} to="/plans" variant="primary">–ü–æ—Å–º–æ—Ç—Ä–µ—Ç—å –≤—Å–µ —Å–µ—Ä–≤–∏—Å—ã</Button>
          </div>
