import React, { useState, useEffect } from 'react';
import { Container, Row, Col, Card, Button, Alert, Badge } from 'react-bootstrap';
import { Link } from 'react-router-dom';

const Dashboard = ({ user }) => {
  const [subscriptions, setSubscriptions] = useState([]);
  const [activeSubscription, setActiveSubscription] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [actionSuccess, setActionSuccess] = useState('');

  useEffect(() => {
    fetchSubscriptions();
  }, []);

  const fetchSubscriptions = async () => {
    setLoading(true);
    setError('');

    try {
      // Fetch all user subscriptions
      const token = localStorage.getItem('token');
      const response = await fetch('/api/subscriptions', {
        headers: {
          'Authorization': `Bearer ${token}`
        }
      });

      const data = await response.json();

      if (!response.ok) {
        throw new Error(data.error || 'Failed to fetch subscriptions');
      }

      setSubscriptions(data.subscriptions || []);

      // Fetch active subscription
      const activeResponse = await fetch('/api/subscriptions/active', {
        headers: {
          'Authorization': `Bearer ${token}`
        }
      });

      if (activeResponse.ok) {
        const activeData = await activeResponse.json();
        setActiveSubscription(activeData.subscription);
      }
    } catch (err) {
      setError(err.message);
    } finally {
      setLoading(false);
    }
  };

  const handleCancelSubscription = async (subscriptionId) => {
    if (!window.confirm('Are you sure you want to cancel this subscription?')) {
      return;
    }

    setLoading(true);
    setError('');
    setActionSuccess('');

    try {
      const token = localStorage.getItem('token');
      const response = await fetch(`/api/subscriptions/${subscriptionId}/cancel`, {
        method: 'PUT',
        headers: {
          'Authorization': `Bearer ${token}`,
          'Content-Type': 'application/json'
        }
      });

      const data = await response.json();

      if (!response.ok) {
        throw new Error(data.error || 'Failed to cancel subscription');
      }

      setActionSuccess('Subscription cancelled successfully');

      // Refresh subscriptions
      fetchSubscriptions();
    } catch (err) {
      setError(err.message);
    } finally {
      setLoading(false);
    }
  };

  const handleToggleAutoRenewal = async (subscriptionId, currentAutoRenew) => {
    setLoading(true);
    setError('');
    setActionSuccess('');

    try {
      const token = localStorage.getItem('token');
      const response = await fetch(`/api/subscriptions/${subscriptionId}/auto-renew`, {
        method: 'PUT',
        headers: {
          'Authorization': `Bearer ${token}`,
          'Content-Type': 'application/json'
        },
        body: JSON.stringify({
          auto_renew: !currentAutoRenew
        })
      });

      const data = await response.json();

      if (!response.ok) {
        throw new Error(data.error || 'Failed to update auto-renewal');
      }

      setActionSuccess(`Auto-renewal ${!currentAutoRenew ? 'enabled' : 'disabled'} successfully`);

      // Refresh subscriptions
      fetchSubscriptions();
    } catch (err) {
      setError(err.message);
    } finally {
      setLoading(false);
    }
  };

  const formatDate = (dateString) => {
    const date = new Date(dateString);
    return date.toLocaleDateString();
  };

  return (
    <Container className="py-5">
      <h1 className="mb-4">Dashboard</h1>

      {error && <Alert variant="danger">{error}</Alert>}
      {actionSuccess && <Alert variant="success">{actionSuccess}</Alert>}

      {loading && (
        <div className="text-center my-5">
          <div className="spinner-border" role="status">
            <span className="visually-hidden">Loading...</span>
          </div>
        </div>
      )}

      {!loading && (
        <>
          <Row className="mb-4">
            <Col>
              <Card className="dashboard-card">
                <Card.Body>
                  <h5 className="mb-3">Active Subscription</h5>

                  {activeSubscription ? (
                    <div>
                      <h4 className="mb-3">{activeSubscription.plan.name}</h4>
                      <p>{activeSubscription.plan.description}</p>

                      <Row className="dashboard-stats">
                        <Col md={3} className="mb-3">
                          <Card className="text-center h-100">
                            <Card.Body>
                              <h6>Status</h6>
                              <Badge bg="success" className="fs-6">Active</Badge>
                            </Card.Body>
                          </Card>
                        </Col>
                        <Col md={3} className="mb-3">
                          <Card className="text-center h-100">
                            <Card.Body>
                              <h6>Price</h6>
                              <p className="mb-0 fs-5">${activeSubscription.plan.price.toFixed(2)}</p>
                            </Card.Body>
                          </Card>
                        </Col>
                        <Col md={3} className="mb-3">
                          <Card className="text-center h-100">
                            <Card.Body>
                              <h6>Renewal Date</h6>
                              <p className="mb-0">{formatDate(activeSubscription.renewal_date)}</p>
                            </Card.Body>
                          </Card>
                        </Col>
                        <Col md={3} className="mb-3">
                          <Card className="text-center h-100">
                            <Card.Body>
                              <h6>Auto Renew</h6>
                              <Badge bg={activeSubscription.auto_renew ? "success" : "secondary"}>
                                {activeSubscription.auto_renew ? "Enabled" : "Disabled"}
                              </Badge>
                            </Card.Body>
                          </Card>
                        </Col>
                      </Row>

                      <div className="d-flex">
                        <Button 
                          variant={activeSubscription.auto_renew ? "outline-warning" : "outline-success"}
                          onClick={() => handleToggleAutoRenewal(activeSubscription.id, activeSubscription.auto_renew)}
                          className="me-2"
                        >
                          {activeSubscription.auto_renew ? "Disable Auto-Renewal" : "Enable Auto-Renewal"}
                        </Button>
                        <Button 
                          variant="danger" 
                          onClick={() => handleCancelSubscription(activeSubscription.id)}
                        >
                          Cancel Subscription
                        </Button>
                      </div>
                    </div>
                  ) : (
                    <div className="text-center py-4">
                      <p>You don't have any active subscriptions.</p>
                      <Button as={Link} to="/plans" variant="primary">
                        Browse Subscription Plans
                      </Button>
                    </div>
                  )}
                </Card.Body>
              </Card>
            </Col>
          </Row>

          {subscriptions.length > 0 && (
            <Row>
              <Col>
                <h3 className="mb-3">Subscription History</h3>

                <div className="table-responsive">
                  <table className="table table-hover">
                    <thead>
                      <tr>
                        <th>Plan</th>
                        <th>Start Date</th>
                        <th>End Date</th>
                        <th>Status</th>
                        <th>Price</th>
                        <th>Auto Renewal</th>
                        <th>Actions</th>
                      </tr>
                    </thead>
                    <tbody>
                      {subscriptions.map(sub => (
                        <tr key={sub.id}>
                          <td>{sub.plan.name}</td>
                          <td>{formatDate(sub.start_date)}</td>
                          <td>{formatDate(sub.end_date)}</td>
                          <td>
                            <Badge bg={
                              sub.status === 'active' ? "success" :
                              sub.status === 'cancelled' ? "warning" : "secondary"
                            }>
                              {sub.status}
                            </Badge>
                          </td>
                          <td>${sub.plan.price.toFixed(2)}</td>
                          <td>
                            <Badge bg={sub.auto_renew ? "success" : "secondary"}>
                              {sub.auto_renew ? "Yes" : "No"}
                            </Badge>
                          </td>
                          <td>
                            {sub.status === 'active' && (
                              <div className="d-flex">
                                <Button
                                  size="sm"
                                  variant={sub.auto_renew ? "outline-warning" : "outline-success"}
                                  onClick={() => handleToggleAutoRenewal(sub.id, sub.auto_renew)}
                                  className="me-2"
                                >
                                  {sub.auto_renew ? "Disable Auto" : "Enable Auto"}
                                </Button>
                                <Button
                                  size="sm"
                                  variant="outline-danger"
                                  onClick={() => handleCancelSubscription(sub.id)}
                                >
                                  Cancel
                                </Button>
                              </div>
                            )}
                          </td>
                        </tr>
                      ))}
                    </tbody>
                  </table>
                </div>
              </Col>
            </Row>
          )}
        </>
      )}
    </Container>
  );
};

export default Dashboard;
